let timer=document.querySelector(".timer");
function update() {
    let now = new Date();//Текущее время
    let nextMarch8th = now.getFullYear();
    let March8th = new Date(nextMarch8th, 2, 8, 0, 0, 0);//8 марта следующего года
    //Разница во времени в миллисекундах 
    let timeDifference = March8th - now;
    let days = Math.floor(timeDifference / (1000 * 60 * 60 * 24));
    let hours = Math.floor((timeDifference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    let minutes = Math.floor((timeDifference % (1000 * 60 * 60)) / (1000 * 60));
    let seconds = Math.floor((timeDifference % (1000 * 60)) / (1000));
    timer.innerHTML = `До 8-ого марта осталось <span class="big"> ${days} </span> дней, <span class="big"> ${hours} </span> часов, <span class="big"> ${minutes} </span> минут, <span class="big"> ${seconds} </span> секунд!`;
}
setInterval(update, 1000);
update();
